import * as brandServices from '../api/BrandServices'
import { SET_BRAND_LIST, SET_ASSOCIATED_BRAND_LIST } from "./types";
import { errors, SweetAlert } from "./../utilities/helpers";
import i18n from "i18next";

export const brandListAction = data => {
  return {
      type: SET_BRAND_LIST,
      payload: data
  };
};

export const getAssociatedAction = data => {
  return {
      type: SET_ASSOCIATED_BRAND_LIST,
      payload: data
  };
};

// get Brands List
export const getBrands = (config, params) => dispatch => {
  const response = brandServices.getBrandsAPI(config, params.start, params.limit, params.search);
    response.then(res => 
      dispatch(brandListAction(res.data))
      )
    .catch(err =>
      errors(this, err)
    );
};

// get Associated Brands List
export const getAssociatedBrands = (config, loginId) => dispatch => {
  const response = brandServices.getAssociatedBrandsAPI(config, loginId);
    response.then(res => 
      dispatch(getAssociatedAction(res.data))
      )
    .catch(err =>
      errors(this, err)
    );
};

// Associate Brands List to OEM
export const associateBrands = (config, params) => dispatch => {
  const response = brandServices.associateBrandsAPI(config, params);
    response.then(res =>{ 
      if(res.status === 200)
      {
        params = {
        title: i18n.t('success'),
        message: res.data.message,
        type: 'success'
      }
      SweetAlert(params); 
     }     
     })
    .catch(err =>
      errors(this, err)
    );
};
